            m_objMenu = new DNNMenu();
            Menu.ID = m_strControlID;
            Menu.EnableViewState = false;
            Menu.NodeClick += DNNMenu_NodeClick;
            Menu.PopulateOnDemand += DNNMenu_PopulateOnDemand;
