{
				string _StringValue = Null.NullString;
                if ((DateValue.ToUniversalTime().Date != (DateTime)SqlDateTime.MinValue && DateValue != Null.NullDate))
				{
					_StringValue = DateValue.ToString(Format);
				}
				return _StringValue;
			}
