{
            Contract.Assert(route != null);
            Contract.Assert(route.Url != null);

            if (route.Constraints == null)
            {
                return;
            }

            foreach (var kvp in route.Constraints.Where(kvp => !(kvp.Value is string)).Where(kvp => !(kvp.Value is IRouteConstraint)))
            {
                throw new InvalidOperationException("Invalid Constraint", new Exception(typeof(IRouteConstraint).FullName));
            }
        }

{
            if (mask == null)
                return false;

            var ba = address.GetAddressBytes();
            var bm = mask.GetAddressBytes();
            var bb = target.GetAddressBytes();

            if (ba.Length != bm.Length || bm.Length != bb.Length)
                return false;

            for (var i = 0; i < ba.Length; i++)
            {
                var m = bm[i];

                var a = ba[i] & m;
                var b = bb[i] & m;

                if (a != b)
                    return false;
            }

            return true;
        }

{
            var szRemoteAddr = request.UserHostAddress;
            var szXForwardedFor = request.ServerVariables["X_FORWARDED_FOR"];
            var szIP = string.Empty;

            if (szXForwardedFor == null)
            {
                szIP = szRemoteAddr;
            }
            else
            {
                szIP = szXForwardedFor;
                if (szIP.IndexOf(",", StringComparison.Ordinal) <= 0) return szIP;
                var arIPs = szIP.Split(',');
                foreach (var item in arIPs.Where(item => !IsPrivateIP(item)))
                {
                    return item;
                }
            }
            return szIP;
        }

{
            var interfaces = NetworkInterface.GetAllNetworkInterfaces();
            var ipAddress = new IPAddress(Encoding.UTF8.GetBytes(address));
            return interfaces.Select(iface => iface.GetIPProperties()).SelectMany(properties => properties.UnicastAddresses).Any(ifAddr => ifAddr.IPv4Mask != null && ifAddr.Address.AddressFamily == AddressFamily.InterNetwork && CheckMask(ifAddr.Address, ifAddr.IPv4Mask, ipAddress));
        }

{
            var providers = GlobalConfiguration.Configuration.GetTabAndModuleInfoProviders();

            foreach (var provider in providers)
            {
                T output;
                if (func(provider, request, out output))
                {
                    return output;
                }
            }

            return fallback;
        }

{
            var component = ComponentFactory.GetComponent<ModuleRoutingProvider>();

            if (component == null)
            {
                component = new StandardModuleRoutingProvider();
                ComponentFactory.RegisterComponentInstance<ModuleRoutingProvider>(component);
            }

            return component;
        }

private readonly Dictionary<string, int> _moduleUsage = new Dictionary<string, int>();
        private readonly RouteCollection _routes;
        private readonly PortalAliasMvcRouteManager _portalAliasMvcRouteManager;

        public MvcRoutingManager() : this(RouteTable.Routes)
        {
        }

        internal MvcRoutingManager(RouteCollection routes)
        {
            _routes = routes;
            _portalAliasMvcRouteManager = new PortalAliasMvcRouteManager();
            TypeLocator = new TypeLocator();
        }

{
            var dictionary = values as IDictionary<string, object>;
            return dictionary != null ? new RouteValueDictionary(dictionary) : TypeHelper.ObjectToDictionary(values);
        }

{
            RegisterSystemRoutes();
            ClearCachedRouteData();

            _moduleUsage.Clear();
            foreach (var routeMapper in GetServiceRouteMappers())
            {
                try
                {
                    routeMapper.RegisterRoutes(this);
                }
                catch (Exception e)
                {
                    Logger.ErrorFormat("{0}.RegisterRoutes threw an exception.  {1}\r\n{2}", routeMapper.GetType().FullName,
                                 e.Message, e.StackTrace);
                }
            }
        }

{
            return MapRoute(moduleFolderName, routeName, url, null /* defaults */, null /* constraints */, namespaces);
        }

{
            return MapRoute(moduleFolderName, routeName, url, defaults, null /* constraints */, namespaces);
        }

{
            if (namespaces == null || namespaces.Length == 0 || String.IsNullOrEmpty(namespaces[0]))
            {
                throw new ArgumentException(Localization.GetExceptionMessage("ArgumentCannotBeNullOrEmpty",
                                                                             "The argument '{0}' cannot be null or empty.",
                                                                             "namespaces"));
            }

            Requires.NotNullOrEmpty("moduleFolderName", moduleFolderName);

            url = url.Trim('/', '\\');

            var prefixCounts = _portalAliasMvcRouteManager.GetRoutePrefixCounts();
            Route route = null;

            if (url == null)
                throw new ArgumentNullException(nameof(url));

            foreach (var count in prefixCounts)
            {
                var fullRouteName = _portalAliasMvcRouteManager.GetRouteName(moduleFolderName, routeName, count);
                var routeUrl = _portalAliasMvcRouteManager.GetRouteUrl(moduleFolderName, url, count);
                route = MapRouteWithNamespace(fullRouteName, routeUrl, defaults, constraints, namespaces);
                _routes.Add(route);
                Logger.Trace("Mapping route: " + fullRouteName + " @ " + routeUrl);
            }

            return route;
        }

{
            var route = new Route(url, new DnnMvcRouteHandler())
            {
                Defaults = CreateRouteValueDictionaryUncached(defaults),
                Constraints = CreateRouteValueDictionaryUncached(constraints)
            };
            if (route.DataTokens == null)
            {
                route.DataTokens = new RouteValueDictionary();
            }
            ConstraintValidation.Validate(route);
            if ((namespaces != null) && (namespaces.Length > 0))
            {
                route.SetNameSpaces(namespaces);
            }
            route.SetName(name);
            return route;
        }

{
            //add standard tab and module id provider
            GlobalConfiguration.Configuration.AddTabAndModuleInfoProvider(new StandardTabAndModuleInfoProvider());
            using (_routes.GetWriteLock())
            {
                //_routes.Clear(); -- don't use; it will remove original WEP API maps
                LocateServicesAndMapRoutes();
            }
            Logger.TraceFormat("Registered a total of {0} routes", _routes.Count);
        }

{
            if (count == 0)
            {
                return "";
            }

            var prefix = "";

            for (var i = count - 1; i >= 0; i--)
            {
                prefix = "{prefix" + i + "}/" + prefix;
            }

            return prefix;
        }

{
            var allRouteValues = new RouteValueDictionary(routeValues);

            var segments = portalAliasInfo.HTTPAlias.Split('/');

            if (segments.Length <= 1) return allRouteValues;
            for (var i = 1; i < segments.Length; i++)
            {
                var key = "prefix" + (i - 1).ToString(CultureInfo.InvariantCulture);
                var value = segments[i];
                allRouteValues.Add(key, value);
            }

            return allRouteValues;
        }

{
            Requires.NotNullOrEmpty("moduleFolderName", moduleFolderName);
            Requires.NotNegative("count", count);

            return moduleFolderName + "-" + routeName + "-" + count.ToString(CultureInfo.InvariantCulture);
        }

{
            var alias = portalAlias.HTTPAlias;
            string appPath = TestableGlobals.Instance.ApplicationPath;
            if (!string.IsNullOrEmpty(appPath))
            {
                int i = alias.IndexOf(appPath, StringComparison.OrdinalIgnoreCase);
                if (i > 0)
                {
                    alias = alias.Remove(i, appPath.Length);
                }
            }
            return GetRouteName(moduleFolderName, routeName, CalcAliasPrefixCount(alias));
        }

{
            if (_prefixCounts != null) return _prefixCounts;
            //prefixCounts are required for each route that is mapped but they only change
            //when a new portal is added so cache them until that time
            var portals = PortalController.Instance.GetPortals();
            var segmentCounts1 = new List<int>();

            foreach (
                var count in
                    portals.Cast<PortalInfo>()
                        .Select(
                            portal =>
                                PortalAliasController.Instance.GetPortalAliasesByPortalId(portal.PortalID)
                                    .Select(x => x.HTTPAlias))
                        .Select(StripApplicationPath)
                        .SelectMany(
                            aliases =>
                                aliases.Select(CalcAliasPrefixCount).Where(count => !segmentCounts1.Contains(count))))
            {
                segmentCounts1.Add(count);
            }
            IEnumerable<int> segmentCounts = segmentCounts1;
            _prefixCounts = segmentCounts.OrderByDescending(x => x).ToList();

            return _prefixCounts;
        }

{
            var appPath = TestableGlobals.Instance.ApplicationPath;

            return string.IsNullOrEmpty(appPath) ? aliases : StripApplicationPathIterable(aliases, appPath);
        }

{
            //Look for a module control
            string controlKey = (routeValues.ContainsKey("ctl")) ? (string)routeValues["ctl"] : String.Empty;

            List<string> additionalParams = (from routeValue in routeValues 
                                             where !ExcludedRouteValues.Split(',').ToList().Contains(routeValue.Key.ToLower()) 
                                             select routeValue.Key + "=" + routeValue.Value)
                                             .ToList();

            string url;
            if (String.IsNullOrEmpty(controlKey))
            {
                additionalParams.Insert(0, "moduleId=" + moduleContext.Configuration.ModuleID);
                url = Globals.NavigateURL("", additionalParams.ToArray());
            }
            else
            {
                url = moduleContext.EditUrl(String.Empty, String.Empty, controlKey, additionalParams.ToArray());
            }

            return url;
        }

{
            var segments = moduleControl.ControlSrc.Replace(".mvc", "").Split('/');
            string routeNamespace = String.Empty;
            string routeControllerName;
            string routeActionName;
            if (segments.Length == 3)
            {
                routeNamespace = segments[0];
                routeControllerName = segments[1];
                routeActionName = segments[2];
            }
            else
            {
                routeControllerName = segments[0];
                routeActionName = segments[1];
            }

            var actionName = (httpContext == null) ? routeActionName : httpContext.Request.QueryString.GetValueOrDefault("action", routeActionName);
            var controllerName = (httpContext == null) ? routeControllerName : httpContext.Request.QueryString.GetValueOrDefault("controller", routeControllerName);

            var routeData = new RouteData();
            routeData.Values.Add("controller", controllerName);
            routeData.Values.Add("action", actionName);

            if (httpContext != null)
            {
                foreach (var param in httpContext.Request.QueryString.AllKeys)
                {
                    if (!ExcludedQueryStringParams.Split(',').ToList().Contains(param.ToLower()))
                    {
                        routeData.Values.Add(param, httpContext.Request.QueryString[param]);
                    }
                }
            }
            if (!String.IsNullOrEmpty(routeNamespace))
            {
                routeData.DataTokens.Add("namespaces", new string[] { routeNamespace });
            }

            return routeData;
        }
