{
            var tags = new List<string>();
            if (string.IsNullOrEmpty(keywords))
            {
                outputKeywords = string.Empty;
            }
            else
            {
                var m = TagRegex.Match(keywords);
                while (m.Success)
                {
                    var tag = m.Groups[1].ToString();
                    if (!string.IsNullOrEmpty(tag))
                    {
                        tags.Add(tag.Trim());
                    }
                    m = m.NextMatch();
                }

                outputKeywords = TagRegex.Replace(keywords, string.Empty).Trim();
            }
            return tags;
        }

{
            if (result.ModuleDefId > 0 && result.ModuleDefId == HtmlModuleDefitionId) //special handling for Html module
