            }

{
                return ViewState["DisabledCssClass"] == null ? string.Empty : (string) ViewState["DisabledCssClass"];
            }

{
                ViewState["DisabledCssClass"] = value;
            }

{
                return _localize;
            }

{
                _localize = value;
            }

{
                return ViewState["Text"] == null ? string.Empty : (string) ViewState["Text"];
            }

{
                ViewState["Text"] = value;
            }

